import { faFilm } from '@fortawesome/free-solid-svg-icons';

@Component({
  selector: 'app-tests',
  templateUrl: './tests.component.html',
  styleUrls: ['./tests.component.scss']
})
export class TestsComponent implements OnInit {
  cateroties = [];
  currLng = "";
  filmIcon = faFilm;

