    this.minutes = 0; //this is time limit witch is stop there.

    this.timeLimit = 0; // this is 

    this.timeTricker = 0;
    this.wpm = "0"; //whole

    this.lwpm = "0"; // mistake count

    this.errors = 0; // it is only count current text value. 

    this.characters = 0;
    this.accuracy = 100;
    this.speedType = 'wpm';
    this.typingProcess = [];
    this.testIndex = 0;
    this.wordIndex = 0;
    this.letterIndex = 0;
    this.words = [];
    this.wrote = new FormControl("");
    this.timer = null;
    this.playSounds = true;
    activatedRoute.queryParams.subscribe(params => {
      if (params['test'] == "1-minute") {
        this.minutes = 1;
        this.seconds = 0;
        this.timeLimit = 60;
        this.timeCountIsUp = false;
      }

      if (params['test'] == "3-minute") {
        this.minutes = 3;
